mapper.select:
SELECT COUNT(*) FROM sm_cut_sensor_data
WHERE cs_state = 0

	AND cs_prod_name LIKE CONCAT ('%', #{searchKeyword}, '%')


	AND YEAR(CS_REG_DATE) = #{searchCondition}


AND MONTH(CS_REG_DATE) = #{searchCondition2}

SELECT * FROM sm_cut_sensor_data WHERE cs_state = 0
AND cs_prod_name LIKE CONCAT ('%', #{searchKeyword}, '%')
AND YEAR(CS_REG_DATE) = #{searchCondition}
AND MONTH(CS_REG_DATE) = #{searchCondition2}
ORDER BY cs_idx desc OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY

SELECT YEAR(CS_REG_DATE) AS years , MONTH(CS_REG_DATE) AS months ,DAY(CS_REG_DATE) AS days ,SUM(CS_WORK_TIME) AS work_time FROM sm_cut_sensor_data WHERE CS_STATE = 0 AND YEAR(CS_REG_DATE) = #{searchCondition} AND MONTH(CS_REG_DATE) = #{searchCondition2}
AND cs_prod_name LIKE CONCAT ('%', #{searchKeyword}, '%')
GROUP BY YEAR(CS_REG_DATE),MONTH(CS_REG_DATE),DAY(CS_REG_DATE)

mapper.update:
update sm_cut_sensor_data set
cs_reg_date = #{csRegDate2}
where cs_idx = #{csIdx2}
