mapper.select:
SELECT COUNT(*) FROM sm_mf_proc
WHERE or_id = #{orId}
AND mp_pr_state in (1,3)

mapper.update:
UPDATE sm_mf_proc_log SET
mfl_manager = #{mflManager},
mfl_st_date = #{mflStDate},
mfl_ed_date = #{mflEdDate}
WHERE mfl_idx = #{mflIdx}

mapper.delete:

update sm_mf_proc set
MP_PR_STATE = 3
where or_id = #{orId}
